import React, {useState} from 'react';  
import {StyleSheet, Text, View} from 'react-native';
import {server} from '../constante';
import axios from 'axios';
import { ListItem} from 'react-native-elements';

class LivraisonsScreen extends React.Component{
    constructor(props){
        super(props);
        this.state={
            mail:'damiendelestienne4@gmail.com',
            tabLiv:[]
        }
    }
    componentDidMount(){
        axios.get(server+'/api/livraisons/mail/'+this.state.mail)
        .then( res => {

            this.setState({tabLiv:res.data});
        })
    }
    render(){
        return (  
                <View style={styles.container}>       
                     {
                            this.state.tabLiv.map((l, i) => (
                            <ListItem key={i} bottomDivider style={{width:"100"}}>
                                <ListItem.Content>
                                <ListItem.Title style={{fontSize:"40%"}}>{l.nom}</ListItem.Title>
                                <ListItem.Subtitle>Numéro de colis: {l.numcolis}</ListItem.Subtitle>
                                <ListItem.Subtitle>Passée le: {new Date(l.datedebut).toLocaleDateString()}</ListItem.Subtitle>
                                <ListItem.Subtitle>Statut: {l.datefin==null ? <Text>Non livré</Text> : <Text>Livré le: {new Date(l.datefin).toLocaleDateString()}</Text>}</ListItem.Subtitle>
                                </ListItem.Content>
                            </ListItem>
                            ))
                    }
                </View>  
            );  
    }
}

const styles = StyleSheet.create({  
    container: {  
        flex: 1,  
         flexDirection: 'column', 
        alignItems: 'center'  
    },  
});  

export default LivraisonsScreen;
